import React, { useEffect, useState } from "react";
import "./PostList.css";

const PostList = () => {
  const [card, setCard] = useState([]);
  const [page, setPage] = useState(1);
  const [loading, setLoading] = useState(false);

  const getCardData = async () => {
    setLoading(true);
    const res = await fetch(
      `https://jsonplaceholder.typicode.com/posts?_limit=6&_page=${page}`
    );
    const data = await res.json();
    setCard((prev) => [...prev, ...data]);
    setLoading(false);
  };

  const handelInfiniteScroll = () => {
    if (
      window.innerHeight + document.documentElement.scrollTop + 1 >=
      document.documentElement.scrollHeight
    ) {
      setPage((prev) => prev + 1);
    }
  };

  useEffect(() => {
    const debouncedScrollHandler = debounce(handelInfiniteScroll, 100);
    window.addEventListener("scroll", debouncedScrollHandler);
    return () => window.removeEventListener("scroll", debouncedScrollHandler);
  }, []);

  useEffect(() => {
    getCardData();
  }, [page]);

  return (
    <div>
      <div className="text-end m-3">
        <button class="group relative p-3 overflow-hidden rounded-2xl bg-green-500 text-lg font-bold text-white">
          Sign out
          <div class="absolute inset-0 h-full w-full scale-0 rounded-2xl transition-all duration-300 group-hover:scale-100 group-hover:bg-white/30"></div>
        </button>
      </div>
      <div className="mx-auto max-w-2xl px-4 py-4 sm:px-6 sm:py-14 lg:max-w-7xl lg:px-8">
        <div className="w-full grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-4 post-container">
          {card.map((post) => (
            <div
              key={post.id}
              className="flex justify-center m-2 bg-[ #ffe3e3]"
            >
              <div className="rounded-xl  border border-[#545863] p-5 shadow-md w-9/12 bg-white">
                <div className="flex w-full items-center justify-between border-b pb-3">
                  <div className="flex items-center space-x-3">
                    <div className="h-8 w-8 rounded-full bg-slate-400 bg-[url('https://i.pravatar.cc/32')]" />
                    <div className="text-lg font-bold text-slate-700">
                      User Id {post.id}
                    </div>
                  </div>
                </div>
                <div className="mt-4 mb-6">
                  <div className="mb-3 text-xl font-bold text-[#F9C846]">
                    {post.title}
                  </div>
                  <div className="text-md text-[#545863]">{post.body}</div>
                </div>
              </div>
            </div>
          ))}
        </div>
        {loading && (
          <div className="flex justify-center m-6">
            <div
              class="w-12 h-12 rounded-full animate-spin
                      border-2 border-solid border-blue-500 border-t-transparent"
            ></div>
          </div>
        )}
      </div>
    </div>
  );
};

export default PostList;

function debounce(func, delay) {
  let timer;
  return function () {
    clearTimeout(timer);
    timer = setTimeout(() => {
      func.apply(this, arguments);
    }, delay);
  };
}
