import React, { useCallback, useState } from "react";
import "./PostList.css";
import { useDispatch, useSelector } from "react-redux";
import { fetchPostsAsync, selectPosts } from "./PostListSlice";
import InfiniteScroll from "react-infinite-scroll-component";
const PostList = () => {
  const post = useSelector(selectPosts);
  const [hasMore, setHasMore] = useState(true);
  const dispatch = useDispatch();
  const fetchMoreData = useCallback(() => {
    if (post.length >= 500) {
      setHasMore(false);
      return;
    }
    dispatch(fetchPostsAsync()); // Dispatch your Redux action to fetch more posts
  }, [dispatch, items.length]);

  useEffect(() => {
    // Initial data fetch (if needed)
    dispatch(fetchPosts());
  }, [dispatch]);
  return (
    <div className="w-full post-container bg-gray-100">
      <div>
        <InfiniteScroll
          dataLength={items.length}
          next={fetchMoreData}
          hasMore={hasMore}
          loader={<h4>Loading...</h4>}
          endMessage={
            <p style={{ textAlign: "center" }}>
              <b>Yay! You have seen it all</b>
            </p>
          }
        ></InfiniteScroll>
        {post.map((post) => (
          <div key={post.id} className="flex items-center justify-center m-2">
            <div className="rounded-xl border border-purple-600 p-5 shadow-md w-9/12 bg-white">
              <div className="flex w-full items-center justify-between border-b pb-3">
                <div className="flex items-center space-x-3">
                  <div className="h-8 w-8 rounded-full bg-slate-400 bg-[url('https://i.pravatar.cc/32')]" />
                  <div className="text-lg font-bold text-slate-700">
                    User Id {post.id}
                  </div>
                </div>
              </div>
              <div className="mt-4 mb-6">
                <div className="mb-3 text-xl font-bold">{post.title}</div>
                <div className="text-sm text-neutral-600">{post.body}</div>
              </div>
              <div>
                <div className="flex items-center justify-between text-slate-500">
                  <div className="flex space-x-4 md:space-x-8">
                    <div className="flex cursor-pointer items-center transition hover:text-slate-600">
                      <svg
                        xmlns="http://www.w3.org/2000/svg"
                        className="mr-1.5 h-5 w-5"
                        fill="none"
                        viewBox="0 0 24 24"
                        stroke="currentColor"
                        strokeWidth={2}
                      >
                        <path
                          strokeLinecap="round"
                          strokeLinejoin="round"
                          d="M7 8h10M7 12h4m1 8l-4-4H5a2 2 0 01-2-2V6a2 2 0 012-2h14a2 2 0 012 2v8a2 2 0 01-2 2h-3l-4 4z"
                        />
                      </svg>
                      <span>125</span>
                    </div>
                    <div className="flex cursor-pointer items-center transition hover:text-indigo-400">
                      <svg
                        xmlns="http://www.w3.org/2000/svg"
                        className="mr-1.5 h-5 w-5"
                        fill="none"
                        viewBox="0 0 24 24"
                        stroke="currentColor"
                        strokeWidth={2}
                      >
                        <path
                          strokeLinecap="round"
                          strokeLinejoin="round"
                          d="M14 10h4.764a2 2 0 011.789 2.894l-3.5 7A2 2 0 0115.263 21h-4.017c-.163 0-.326-.02-.485-.06L7 20m7-10V5a2 2 0 00-2-2h-.095c-.5 0-.905.405-.905.905 0 .714-.211 1.412-.608 2.006L7 11v9m7-10h-2M7 20H5a2 2 0 01-2-2v-6a2 2 0 012-2h2.5"
                        />
                      </svg>
                      <span>4</span>
                    </div>
                  </div>
                </div>
              </div>
            </div>
          </div>
        ))}
      </div>
    </div>
  );
};

export default PostList;
