import React, { useEffect, useState } from "react";
import "./PostList.css";
import InfiniteScroll from "react-infinite-scroll-component";

const PostList = () => {
  const [card, setCard] = useState([]);
  const [page, setPage] = useState(1);

  const getCardData = async () => {
    const res = await fetch(
      `https://jsonplaceholder.typicode.com/posts?_limit=6&_page=${page}`
    );
    const data = await res.json();
    setCard((prev) => [...prev, ...data]);
    setPage((prev) => prev + 1);
    setHasMore(data.length > 0);
  };

  useEffect(() => {
    getCardData();
  }, [page]);

  const handelInfiniteScroll = async () => {
    try {
      if (
        window.innerHeight + document.documentElement.scrollTop + 1 >=
        document.documentElement.scrollHeight
      ) {
        setPage((prev) => prev + 1);
      }
    } catch (error) {
      console.log(error);
    }
  };

  useEffect(() => {
    window.addEventListener("scroll", handelInfiniteScroll);
    return () => window.removeEventListener("scroll", handelInfiniteScroll);
  }, []);
  return (
    <div className="w-full post-container">
      <InfiniteScroll
        dataLength={card.length}
        next={getCardData}
        hasMore={hasMore}
        loader={<h4>Loading...</h4>}
      ></InfiniteScroll>
      {card.map((post) => (
        <div key={post.id} className="flex items-center justify-center m-2">
          <div className="rounded-xl border border-[#545863] p-5 shadow-md w-9/12 bg-white">
            <div className="flex w-full items-center justify-between border-b pb-3">
              <div className="flex items-center space-x-3">
                <div className="h-8 w-8 rounded-full bg-slate-400 bg-[url('https://i.pravatar.cc/32')]" />
                <div className="text-lg font-bold text-slate-700">
                  User Id {post.id}
                </div>
              </div>
            </div>
            <div className="mt-4 mb-6">
              <div className="mb-3 text-xl font-bold text-[#F9C846]">
                {post.title}
              </div>
              <div className="text-md text-[#545863]">{post.body}</div>
            </div>
          </div>
        </div>
      ))}
    </div>
  );
};

export default PostList;
