import React from "react";
import { useForm } from "react-hook-form";
const Signup = () => {
  const {
    register,
    handleSubmit,
    formState: { errors },
  } = useForm();
  return (
    <div>
      <div className="min-h-screen flex items-center justify-center bg-gray-100">
        <div className="bg-white p-8 rounded-md shadow-md w-96">
          <h2 className="text-2xl font-bold mb-4 text-center">Signup</h2>
          <form
            onSubmit={handleSubmit((data) => {
              console.log(data);
            })}
          >
            <div className="mb-4">
              <label className="block text-gray-700 mb-1" htmlFor="username">
                Full Name
              </label>
              <input
                className="w-full border border-gray-300 rounded-md px-3 py-2 focus:outline-none focus:border-blue-500"
                type="text"
                id="name"
                placeholder="Full name"
                {...register("name", { required: true })}
              />
              {errors.name?.type === "required" && (
                <p className="text-red-500 text-xs">Full name is required</p>
              )}
            </div>
            <div className="mb-4">
              <label className="block text-gray-700 mb-1" htmlFor="username">
                Username or email
              </label>
              <input
                className="w-full border border-gray-300 rounded-md px-3 py-2 focus:outline-none focus:border-blue-500"
                type="text"
                id="username"
                placeholder="Username"
                {...register("username", { required: true })}
              />
              {errors.username?.type === "required" && (
                <p className="text-red-500 text-xs">
                  Username or email is required
                </p>
              )}
            </div>
            <div className="mb-4">
              <label className="block text-gray-700 mb-1" htmlFor="username">
                Password
              </label>
              <input
                className="w-full border border-gray-300 rounded-md px-3 py-2 focus:outline-none focus:border-blue-500"
                type="text"
                id="password"
                placeholder="Password"
                {...register("password", { required: true })}
              />
              {errors.password?.type === "required" && (
                <p className="text-red-500 text-xs">Password is required</p>
              )}
            </div>
            {/* Add more input fields like email, password, etc. with similar structure */}

            <div className="mb-4">
              <label className="block text-gray-700 mb-1" htmlFor="termsAgreed">
                Agree to Terms & Conditions
              </label>
              <input
                className="mr-2"
                type="checkbox"
                id="termsAgreed"
                {...register("termsAgreed", { required: true })}
              />
              {errors.termsAgreed && (
                <p className="text-red-500 text-xs">
                  You must agree to the terms
                </p>
              )}
            </div>

            <button
              type="submit"
              className="bg-blue-500 hover:bg-blue-700 text-white font-medium py-2 px-4 rounded-md w-full"
            >
              Sign Up
            </button>
          </form>
        </div>
      </div>
    </div>
  );
};

export default Signup;
