import React, { useCallback, useEffect, useState } from "react";
import "./PostList.css";
import axios from "axios";
import InfiniteScroll from "react-infinite-scroll-component";
import { useDispatch, useSelector } from "react-redux";
import { fetchPostsAsync, selectPosts } from "./PostListSlice";

const PostList = () => {
  const post = useSelector(selectPosts);
  const dispatch = useDispatch();
  const [posts, setPosts] = useState([]);
  const [hasMore, setHasMore] = useState(true);
  const [page, setPage] = useState(1);

  useEffect(() => {
    fetchPosts();
  }, [fetchPosts]);

  const fetchPosts = async () => {
    try {
      const response = await axios.get(
        `http://localhost:3000/posts?page=${page}`
      );
      const newPosts = response.data; // Assuming your API returns an array of posts
      setPosts((prevPosts) => [...prevPosts, ...newPosts]);
      setPage(page + 1);
    } catch (error) {
      console.error("Error fetching posts:", error);
    }
  };

  const handleLoadMore = () => {
    fetchPosts();
  };

  useEffect(() => {
    dispatch(fetchPostsAsync());
  }, [dispatch]);
  return (
    <div className="w-full post-container">
      {post &&
        post.map((post) => (
          <div key={post.id} className="flex items-center justify-center m-2">
            <div className="rounded-xl border border-[#545863] p-5 shadow-md w-9/12 bg-white">
              <div className="flex w-full items-center justify-between border-b pb-3">
                <div className="flex items-center space-x-3">
                  <div className="h-8 w-8 rounded-full bg-slate-400 bg-[url('https://i.pravatar.cc/32')]" />
                  <div className="text-lg font-bold text-slate-700">
                    User Id {post.id}
                  </div>
                </div>
              </div>
              <div className="mt-4 mb-6">
                <div className="mb-3 text-xl font-bold text-[#F9C846]">
                  {post.title}
                </div>
                <div className="text-md text-[#545863]">{post.body}</div>
              </div>
              <div>
                <div className="flex items-center justify-between text-slate-500">
                  <div className="flex space-x-4 md:space-x-8">
                    <div className="flex cursor-pointer items-center transition hover:text-slate-600">
                      <svg
                        xmlns="http://www.w3.org/2000/svg"
                        className="mr-1.5 h-5 w-5"
                        fill="none"
                        viewBox="0 0 24 24"
                        stroke="currentColor"
                        strokeWidth={2}
                      >
                        <path
                          strokeLinecap="round"
                          strokeLinejoin="round"
                          d="M7 8h10M7 12h4m1 8l-4-4H5a2 2 0 01-2-2V6a2 2 0 012-2h14a2 2 0 012 2v8a2 2 0 01-2 2h-3l-4 4z"
                        />
                      </svg>
                      <span>125</span>
                    </div>
                    <div className="flex cursor-pointer items-center transition hover:text-indigo-400">
                      <svg
                        xmlns="http://www.w3.org/2000/svg"
                        className="mr-1.5 h-5 w-5"
                        fill="none"
                        viewBox="0 0 24 24"
                        stroke="currentColor"
                        strokeWidth={2}
                      >
                        <path
                          strokeLinecap="round"
                          strokeLinejoin="round"
                          d="M14 10h4.764a2 2 0 011.789 2.894l-3.5 7A2 2 0 0115.263 21h-4.017c-.163 0-.326-.02-.485-.06L7 20m7-10V5a2 2 0 00-2-2h-.095c-.5 0-.905.405-.905.905 0 .714-.211 1.412-.608 2.006L7 11v9m7-10h-2M7 20H5a2 2 0 01-2-2v-6a2 2 0 012-2h2.5"
                        />
                      </svg>
                      <span>4</span>
                    </div>
                  </div>
                </div>
              </div>
            </div>
          </div>
        ))}
    </div>
  );
};

export default PostList;
